// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity >= 0.5.8;

contract prova{
uint256 counterCanzoni;
uint Anno;
address owner;
uint balanceAnnuale;

struct Canzone {
uint256 id;
string name;
address currentOwner;
uint256 listens;
}

// ogni canzone ha un suo proprietario
mapping(uint256 => Canzone) public allCanzoni;
mapping (address => bool) utentiRegistrati;
mapping (address => bool) piattaforme;
Canzone[] classifica;

constructor() payable public{
owner = msg.sender;
Anno = block.timestamp + 365 days;
balanceAnnuale = 0;
}

function Registrati() external payable{ 
require(utentiRegistrati[msg.sender] == false, "sei registrato");
utentiRegistrati[msg.sender] = true;
//pagamento
//payable(owner).transfer(1 ether);

}

function AddPiattaforme() external{
  piattaforme[msg.sender] = true;

}

// add canzone
function addCanzone(string calldata _name) external {
// controlla che l'indirizzo del chiamante è diverso da 0
require(msg.sender != address(0));
//controlla che il chiamante sia un utente registrato
require(utentiRegistrati[msg.sender] == true, "Non sei registrato");
// incrementa il contatore delle canzoni
counterCanzoni++; 
// crea la canzone
Canzone memory newCanzone = Canzone(counterCanzoni, _name, msg.sender,0);
allCanzoni[counterCanzoni] = newCanzone;
}

function addAscolti(uint256 id, uint256 listens) external{
  // ottieni ascolti
  require(piattaforme[msg.sender] == true, "Non sei registrato");
  allCanzoni[id].listens = listens;

}

function ordinaClassifica() private{
  for (uint256 i = 1;i<counterCanzoni;i++){
      classifica[i] = allCanzoni[i];
  }
  for (uint256 i = 1;i<counterCanzoni;i++){
    Canzone memory value= classifica[i];
    uint256 j = i-1;
    while (j>= 0 && classifica[j].listens> value.listens){
      classifica[j+1] = classifica[j];
      j = j-1;
    }
    classifica[j+1] = value;
  }
}

function pagamentoClassifica() payable public {
require(block.timestamp >= Anno);
ordinaClassifica();
//30% a noi
balanceAnnuale -= balanceAnnuale * 30/100 ;
//20% che verrà spartito tra i top 1000
uint256 value = balanceAnnuale * 20/100 ; 
for (uint256 i = 0; i< 1000;i++){
//20% ai primi mille

//payable(classifica[i].currentOwner).transfer(value/1000);
}
//il resto spertito tra tutti gli altri
balanceAnnuale -= value;
for (uint256 i = 0; i< counterCanzoni;i++){

//payable(classifica[i].currentOwner).transfer(balanceAnnuale/counterCanzoni);
}
balanceAnnuale = 0;
Anno =block.timestamp + 365 days;


}

function pagaUtilizzoCanzone(/*uint256 id*/) external payable{
uint256 costo = 3 ether;

//payable(owner).transfer(costo);

balanceAnnuale += costo;
}



}
